format_factorization &lt;- function(df) {
  stopifnot("factor" %in% names(df), "power" %in% names(df))

  paste(paste0(df$factor, "^", df$power), collapse = " x ")
}





def format_factorization(df):
  mystr = df.factor.astype("str") + "^" + df.power.astype("str")
  return " x ".join(mystr)

